GP_slot = #{GP_slot}, GP_basetime = #{GP_basetime}, GP_basepay = #{GP_basepay}, GP_overtime = #{GP_overtime}, GP_overpay = #{GP_overpay}, GP_onepay = #{GP_onepay}, GP_monthpay = #{GP_monthpay}
where GP_id = #{GP_id}
